import type { ExtendableAutoFormProps } from '@bwalkt/react'
import type { FieldValues } from 'react-hook-form'

export interface AutoFormProps<T extends FieldValues> extends ExtendableAutoFormProps<T> {}

import type React from 'react'
import { Button } from '@/components/ui/button'

export const SubmitButton: React.FC<{ children: React.ReactNode }> = ({ children }) => (
  <Button type="submit">{children}</Button>
)

import type { AutoFormFieldProps } from '@bwalkt/react'
import type React from 'react'
import { Select, SelectItem } from '@/elements'

export const SelectField: React.FC<AutoFormFieldProps> = ({ field, inputProps, error, id: _id }) => {
  const { key, ...props } = inputProps

  return (
    <Select
      id={_id}
      onValueChange={value => {
        const syntheticEvent = {
          target: {
            value,
            name: field.key,
          },
        } as React.ChangeEvent<HTMLInputElement>
        props.onChange(syntheticEvent)
      }}
      value={props.value || field.default}
      color={error ? 'error' : 'default'}
      variant="outline"
      size="2"
      placeholder="Select an option"
    >
      {(field.options || []).map(([key, label]) => (
        <SelectItem key={key} value={key}>
          {label}
        </SelectItem>
      ))}
    </Select>
  )
}

import type { ObjectWrapperProps } from '@bwalkt/react'
import type React from 'react'

export const ObjectWrapper: React.FC<ObjectWrapperProps> = ({ label, children }) => {
  return (
    <div className="space-y-4">
      <h3 className="text-lg font-medium">{label}</h3>
      {children}
    </div>
  )
}

import React from 'react'

export const Form = React.forwardRef<HTMLFormElement, React.ComponentProps<'form'>>(({ children, ...props }, ref) => {
  return (
    <form ref={ref} className="space-y-4" {...props}>
      {children}
    </form>
  )
})

import type { FieldWrapperProps } from '@bwalkt/react'
import type React from 'react'
import { Label } from '@/components/ui/label'

const DISABLED_LABELS = ['boolean', 'object', 'array']

export const FieldWrapper: React.FC<FieldWrapperProps> = ({ label, children, id, field, error }) => {
  const isDisabled = DISABLED_LABELS.includes(field.type)

  return (
    <div className="space-y-2">
      {!isDisabled && (
        <Label htmlFor={id}>
          {label}
          {field.required && <span className="text-destructive"> *</span>}
        </Label>
      )}
      {children}
      {field.fieldConfig?.description && (
        <p className="text-sm text-muted-foreground">{field.fieldConfig.description}</p>
      )}
      {error && <p className="text-sm text-destructive">{error}</p>}
    </div>
  )
}

import type { AutoFormFieldProps } from '@bwalkt/react'
import type React from 'react'
import { Checkbox } from '@/components/ui/checkbox'
import { Label } from '../../label'

export const BooleanField: React.FC<AutoFormFieldProps> = ({ field, label, id, inputProps }) => (
  <div className="flex items-center space-x-2">
    <Checkbox
      id={id}
      onCheckedChange={checked => {
        // react-hook-form expects an event object
        const event = {
          target: {
            name: field.key,
            value: checked,
          },
        }
        inputProps.onChange(event)
      }}
      checked={inputProps.value}
    />
    <Label htmlFor={id}>
      {label}
      {field.required && <span className="text-destructive"> *</span>}
    </Label>
  </div>
)

import type { ArrayWrapperProps } from '@bwalkt/react'
import { PlusIcon } from 'lucide-react'
import type React from 'react'
import { Button } from '@/components/ui/button'

export const ArrayWrapper: React.FC<ArrayWrapperProps> = ({ label, children, onAddItem }) => {
  return (
    <div className="space-y-4">
      <h3 className="text-lg font-medium">{label}</h3>
      {children}
      <Button onClick={onAddItem} variant="outline" size="sm" type="button">
        <PlusIcon className="h-4 w-4" />
      </Button>
    </div>
  )
}

import type { ArrayElementWrapperProps } from '@bwalkt/react'
import { TrashIcon } from 'lucide-react'
import type React from 'react'
import { Button } from '@/components/ui/button'

export const ArrayElementWrapper: React.FC<ArrayElementWrapperProps> = ({ children, onRemove }) => {
  return (
    <div className="relative border p-4 rounded-md mt-2">
      <Button onClick={onRemove} variant="ghost" size="sm" className="absolute top-2 right-2" type="button">
        <TrashIcon className="h-4 w-4" />
      </Button>
      {children}
    </div>
  )
}
